%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% Função mérito - ErroQMDMT(Km,pm) = sum_0^Np (y - ym).^2%%%%  MD - modelo%%  MT - motor%%%% Referências: https://youtu.be/Q832jvYdnzY%%%% Criando a função:function [ErroQ] = ErroQMDMT(X,velocidade,entrada,tempo)%%% o nome do arquivo deve ser ErroQMDMT%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  Dados coletados em laboratório %%   %%   velocidade, entrada e tempo%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  Gerando dados a partir do modelo metamático %%   %%% receber os valores de Km e pmKm = X(1);      % valor do ganhopm = X(2);      % valor do polo%%% crio a função de transferênciaGm = tf(Km, [1 pm]);%%% Visualizar o resultado do modelo comparado aos dadosSYS = Gm;                       % sistema a ser simuladoU   = entrada;                  % entrada do sistemaT   = tempo;                    % vetor tempo%%%% ym - saída%%%%%% CI - condição inicial[ym, T, CI] = lsim (SYS, U, T);%%% Calculando o erro quadráticoErroQ = sum((ym - velocidade).^2)